    A.B a_b=new A().new B();
    A a=new A();
    A.B ab=a.new B();

    //创建静态内部类Inner的对象oi，不需要外部类Outer的对象
    Outer.Inner oi=new Outer.Inner();
    //Outer.Inner oi2=Outer.new Inner();//!!error
    //Outer.Inner oi3=new Outer().new Inner();//!!error
